import moment from 'moment';

export const toCapitalize = (word:string) => {
    return word.charAt(0).toUpperCase() + word.slice(1).toLowerCase()
}

export const dateFormater = (date:string) => {
    return moment(date).format('MMMM D, YYYY')
}
