import { IData } from "../interfaces/IData";
import VideoProjectServices from "../services/VideoProjectService";
import dataList from '../constants/db.json'

export const liveSearch = (keyword:string, data:any) => {
    let isAdvanceSearch = keyword.includes("is:");
    let result = []

    if(isAdvanceSearch){
        const keywordAdvance = keyword.split("is:")
        for (const list of dataList) {
            for (const key of keywordAdvance) {
                if(list.status.toLowerCase() === key){
                    result.push(list)
                }
            }
        }
    } else {
        for (var i = 0; i < data.length; i++) {
            if(data[i].name.toLowerCase().includes(keyword.toLowerCase())) {
                result.push(data[i])
            }
        }
    }


    return result
}
